/* <tlo> */
.bodyWrapper, .sidebar {
    background: #171717 !important;
}

.skinPrev-0 {
    background: #111 !important;
    border-color: #666 !important;
}
.skin--0 /* nowy ciemnoszary */ {
    background: #111 !important;
    border-color: #666 !important;    
}
.skinPrev-1 {
    background: #222 !important;
    border-color: #666 !important;
}
.skin--1 /* nowy szary */ {
    background: #222 !important;
    border-color: #666 !important;    
}
.skinPrev-2 {
    background: #333 !important;
    border-color: #666 !important;
}
.skin--2 /* nowy jasnoszary */ {
    background: #333 !important;
    border-color: #666 !important;    
}

.stdPara1[for=skin-0] span {
    display: none !important;
}
.stdPara1[for=skin-0]:after {
    content: 'ciemnoszary' !important;
}

.stdPara1[for=skin-1] span {
    display: none !important;
}
.stdPara1[for=skin-1]:after {
    content: 'szary' !important;
}

.stdPara1[for=skin-2] span {
    display: none !important;
}
.stdPara1[for=skin-2]:after {
    content: 'jasnoszary' !important;
}

/* ciemnoszary */
body {  
    background: #111 !important;
    color: #aaa !important;
}

/* szary */
.skin-1:not(.screening) body {
    background: #222 !important;
    color: #aaa !important;
}

/* jasnoszary */
.skin-2:not(.screening) body {
    background: #333 !important;
    color: #aaa !important;
}
/* </tlo> */

